import React, { useState } from 'react';
import { gaps } from '../data/gaps';

function Assessment({ onSubmit }) {
  const [selections, setSelections] = useState({});

  const handleCheckboxChange = (gapId, statementIndex) => {
    setSelections(prev => ({
      ...prev,
      [gapId]: {
        ...prev[gapId],
        [statementIndex]: !prev[gapId]?.[statementIndex]
      }
    }));
  };

  const handleSubmit = () => {
    onSubmit(selections);
  };

  return (
    <div className="assessment-container">
      <h2>Assessment</h2>
      <p className="instructions">
        Check any statements that apply to your organization:
      </p>

      {Object.values(gaps).map(gap => (
        <div key={gap.id} className="gap-section">
          <h3>{gap.title}</h3>
          {gap.statements.map((statement, index) => (
            <label key={index} className="statement-label">
              <input
                type="checkbox"
                checked={selections[gap.id]?.[index] || false}
                onChange={() => handleCheckboxChange(gap.id, index)}
              />
              <span>{statement}</span>
            </label>
          ))}
        </div>
      ))}

      <button 
        className="primary-button"
        onClick={handleSubmit}
      >
        See My Results
      </button>
    </div>
  );
}

export default Assessment;
